package wiki.hadoop.io;import org.apache.hadoop.io.Writable;import org.apache.hadoop.mapred.JobConf;import org.apache.hadoop.mapred.Partitioner;public class KeyTextOrderIntPartitioner implements Partitioner<KeyTextOrderIntWritable, Writable> {    @Override  public int getPartition(KeyTextOrderIntWritable key, Writable value, int numPartitions) {    return Math.abs(key.getKey().hashCode()) % numPartitions;  }  @Override  public void configure(JobConf job) {  }  }